postData.query:
query Cart($cityId: Int) {
  cart {
    ...CartFragmentWithCityId
    __typename
  }
}

fragment CartFragmentWithCityId on CartItem {
  ...CartBaseFragment
  sku {
    stock(cityId: $cityId) {
      deliveryDays
      deliveryDate
      deliveryTitle
      type
      warehouseTitle
      status
      statusTitle
      __typename
    }
    __typename
  }
  __typename
}

fragment CartBaseFragment on CartItem {
  amount
  sku {
    availableAmount
    bestPrice
    characteristicValues {
      characteristic {
        id
        title
        type
        values {
          id
          title
          value
          __typename
        }
        __typename
      }
      id
      title
      value
      __typename
    }
    priceBlock {
      ...PriceBlockFragment
      __typename
    }
    discount {
      discountAmount
      discountPrice
      fullDiscountPercent
      modalHeader
      modalText
      paymentOptionKey
      priceText
      sellDiscountPercent
      __typename
    }
    fullPrice
    id
    photo {
      key
      link(trans: PRODUCT_240) {
        high
        low
        __typename
      }
      original {
        high
        low
        __typename
      }
      __typename
    }
    product {
      id
      offer {
        due
        icon
        text
        textColor
        __typename
      }
      oversized
      rating
      shop {
        avatar {
          high
          low
          __typename
        }
        feedbackQuantity
        id
        official
        ordersQuantity
        rating
        seller {
          accountId
          legalRecords {
            name
            value
            __typename
          }
          __typename
        }
        shortTitle
        title
        url
        __typename
      }
      title
      __typename
    }
    sellPrice
    vat {
      price
      type
      vatAmount
      vatRate
      __typename
    }
    __typename
  }
  __typename
}

fragment PriceBlockFragment on PriceBlock {
  badges {
    id
    text
    textColor
    backgroundColor
    __typename
  }
  sellPrice {
    ...priceFragment
    __typename
  }
  finalPrice {
    ...priceFragment
    __typename
  }
  fullPrice {
    ...priceFragment
    __typename
  }
  sellerPrice {
    ...priceFragment
    __typename
  }
  icon
  __typename
}

fragment priceFragment on Price {
  amountColor
  amount
  description
  descriptionColor
  __typename
}
