postData.query:
query getMakeSearch($queryInput: MakeSearchQueryInput!) {
  makeSearch(query: $queryInput) {
    id
    queryId
    queryText
    category {
      ...CategoryShortFragment
      __typename
    }
    items {
      adMarker {
        marker
        __typename
      }
      catalogCard {
        __typename
        ...SkuGroupCardFragment
        discount {
          discountAmount
          discountPrice
          fullDiscountPercent
          modalHeader
          modalText
          paymentOptionKey
          priceText
          sellDiscountPercent
          __typename
        }
      }
      bidId
      __typename
    }
    facets {
      ...FacetFragment
      __typename
    }
    fastFacets {
      ...FacetFragment
      __typename
    }
    total
    mayHaveAdultContent
    categoryFullMatch
    offerCategory {
      title
      title_ru
      title_uz
      id
      __typename
    }
    correctedQueryText
    categoryWasPredicted
    fastCategories {
      category {
        ...FastCategoryFragment
        __typename
      }
      total
      __typename
    }
    permanentLinkSeo {
      id
      seoHeader
      seoMetaTag
      seoTitle
      link {
        ru
        uz
        __typename
      }
      __typename
    }
    token
    todayDealOfferSelectors {
      endDate
      offerId
      startDate
      title
      category {
        id
        title_uz
        title_ru
        title
        adult
        icon
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment FacetFragment on Facet {
  filter {
    id
    title
    type
    measurementUnit
    description
    __typename
  }
  buckets {
    filterValue {
      id
      description
      image
      name
      __typename
    }
    total
    __typename
  }
  range {
    min
    max
    __typename
  }
  __typename
}

fragment CategoryShortFragment on Category {
  id
  parent {
    id
    title
    title_ru
    title_uz
    __typename
  }
  seo {
    permanentLinksSeo {
      id
      seoHeader
      seoMetaTag
      seoTitle
      link {
        ru
        uz
        __typename
      }
      __typename
    }
    __typename
  }
  title
  title_ru
  title_uz
  __typename
}

fragment FastCategoryFragment on Category {
  id
  parent {
    id
    title
    __typename
  }
  title
  seo {
    header
    metaTag
    __typename
  }
  __typename
}

fragment SkuGroupCardFragment on SkuGroupCard {
  ...DefaultCardFragment
  carrierCode
  infoLabel {
    color
    title
    __typename
  }
  photos {
    key
    link(trans: PRODUCT_540) {
      high
      low
      __typename
    }
    previewLink: link(trans: PRODUCT_240) {
      high
      low
      __typename
    }
    __typename
  }
  badges {
    ... on BottomTextBadge {
      __typename
      backgroundColor
      description
      id
      link
      text
      textColor
    }
    ... on UzumInstallmentTitleBadge {
      __typename
      backgroundColor
      text
      id
      textColor
    }
    ... on BottomIconTextBadge {
      __typename
      backgroundColor
      text
      id
      textColor
      iconLink
    }
    __typename
  }
  characteristicValues {
    id
    value
    title
    characteristic {
      values {
        id
        title
        value
        __typename
      }
      title
      id
      __typename
    }
    __typename
  }
  __typename
}

fragment DefaultCardFragment on CatalogCard {
  discount {
    discountPrice
    discountAmount
    paymentOptionKey
    sellDiscountPercent
    __typename
  }
  adult
  feedbackQuantity
  id
  minFullPrice
  minSellPrice
  offer {
    due
    icon
    text
    textColor
    __typename
  }
  badges {
    id
    backgroundColor
    text
    textColor
    ... on BottomIconTextBadge {
      iconLink
      __typename
    }
    __typename
  }
  discountInfo {
    text
    textColor
    backgroundColor
    __typename
  }
  ordersQuantity
  productId
  rating
  title
  buyingOptions {
    defaultSkuId
    isBestPrice
    isSingleSku
    priceBlock {
      ...PriceBlockFragment
      __typename
    }
    deliveryOptions {
      shortDate
      stockType
      textDeliveryWithDate
      __typename
    }
    __typename
  }
  __typename
}

fragment PriceBlockFragment on PriceBlock {
  badges {
    id
    text
    textColor
    backgroundColor
    __typename
  }
  sellPrice {
    ...priceFragment
    __typename
  }
  finalPrice {
    ...priceFragment
    __typename
  }
  fullPrice {
    ...priceFragment
    __typename
  }
  sellerPrice {
    ...priceFragment
    __typename
  }
  icon
  __typename
}

fragment priceFragment on Price {
  amountColor
  amount
  description
  descriptionColor
  __typename
}
